00136 bool GMysqlClient::MysqlQuery(char * ptr, size_t len)
00137 {
00138     if(mysql_state < 2 || !ptr || !ptr[0] || !len || len > 1024 )
00139     return false;
00140 
00141     bool res = true;
00142     pthread_mutex_lock(&mysql_mutex);
00143     while ( mysql_next_result(DBMysql) == 0 );
00144     query_state = mysql_real_query(DBMysql,ptr,len);
00145     if (query_state !=0)
00146     {
00147         std::cout << mysql_error(DBMysql) << std::endl;
00148         res = false;
00149     }
00150     pthread_mutex_unlock(&mysql_mutex);
00151     return res;
00152 }
