00395      inline void Auth0xMD5(char * out, char * in)
00396      {
00397          int nt = 0;
00398          if(in[0] == '0' && in[1] == 'x') in += 2;
00399          for(int i = 0; i < 16; ++i)
00400          {
00401              sscanf(&in[i*2], "%02x", &nt);
00402              out[i] = (char)nt;
00403          }
00404      }
